"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/button"
import { Card, CardContent } from "@/components/card"
import Image from "next/image"
import ErrorModal from "@/components/ErrorModal"

const wallets = [
  { name: "MetaMask", icon: "data:image/png;base64," },
  { name: "WalletConnect(V2)", icon: "data:image/png;base64," },
  { name: "Coinbase Wallet", icon: "data:image/png;base64," },
  { name: "Safe", icon: "data:image/png;base64," },
  { name: "Delegate", icon: "data:image/png;base64," },
  { name: "Solana", icon: "data:image/jpeg;base64," },
  { name: "OpenSea", icon: "data:image/png;base64," },
  { name: "Dapper", icon: "data:image/png;base64," },
  { name: "Blocto", icon: "data:image/png;base64," },
  // ... (other wallets)
]

export default function Page() {
  const [showGM, setShowGM] = useState(true)
  const [isErrorModalOpen, setIsErrorModalOpen] = useState(false)

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowGM(false)
    }, 1000)

    return () => clearTimeout(timer)
  }, [])

  const handleWalletClick = () => {
    setIsErrorModalOpen(true)
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-400 via-blue-500 to-purple-600">
      {showGM && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-yellow-400 transition-opacity duration-500">
          <h1 className="text-6xl font-bold text-white">GM GM!!</h1>
        </div>
      )}
      <header className="sticky top-0 z-40 bg-yellow-400 p-2 text-center text-sm font-medium">
        Always verify the address is https://connect.collab.land
      </header>
      <main className="min-h-screen">
        <Card className="mx-auto max-w-4xl overflow-hidden rounded-3xl bg-white shadow-xl">
          <CardContent className="p-6">
            <div className="mb-8 flex items-center space-x-2">
              <Image
                src="data:image/png;base64,"
                alt="Collab.Land Logo"
                width={32}
                height={32}
              />
              <h1 className="text-2xl font-bold">Collab.Land</h1>
            </div>
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 md:grid-cols-3">
              {wallets.map((wallet) => (
                <Button
                  key={wallet.name}
                  variant="outline"
                  className="flex items-center justify-start space-x-2 rounded-full py-6 text-left transition-all duration-200 hover:bg-gray-100 hover:shadow-md"
                  onClick={handleWalletClick}
                >
                  <Image
                    src={wallet.icon}
                    alt={`${wallet.name} icon`}
                    width={24}
                    height={24}
                  />
                  <span>{wallet.name}</span>
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>
      </main>
      <footer className="mt-8 text-center text-sm text-white">
        <a href="#" className="mr-4 hover:underline">
          Privacy Policy
        </a>
        <a href="#" className="mr-4 hover:underline">
          Terms
        </a>
        <span> 2023 Collab.Land</span>
      </footer>
      <ErrorModal isOpen={isErrorModalOpen} onClose={() => setIsErrorModalOpen(false)} />
    </div>
  )
}